import Head from 'next/head'

class Form extends React.Component {
    constructor(props) {
      super(props);
      this.state = {value: ''};

      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
      this.setState({value: event.target.value});
    }

    handleSubmit(event) {
      event.preventDefault();
      fetch('/influenceurs', { 
        method: 'POST',
        data: {
          commerce: this.ref.commerce,
          product: this.ref.product,
          gender: this.ref.gender,
          audience: this.ref.audience,
          price: this.ref.price,
          app: this.ref.app
        }
      })
    }

    render() {
      return (
        <form onSubmit={this.handleSubmit}>
            <div className="row">
                <label>E-commerce</label>
                <input type="text" ref="commerce" value={this.state.value} onChange={this.handleChange} />
            </div>
            <div className="row">
                <label>Type de Produit/ Marque</label>
                <input type="text" ref="product" value={this.state.value} onChange={this.handleChange} />
            </div>
            <div className="row">
                <label>Genre recherché</label>
                <input type="text" ref="gender" value={this.state.value} onChange={this.handleChange} />
            </div>
            <div className="row">
                <label>Public ciblé</label>
                <input type="text" ref="audience" value={this.state.value} onChange={this.handleChange} />
            </div>
            <div className="row">
                <label>Tarif de l'influenceur</label>
                <input type="text" ref="price" value={this.state.value} onChange={this.handleChange} />
            </div>
            <div className="row">
                <label>Application souhaitée</label>
                <input type="text" ref="app" value={this.state.value} onChange={this.handleChange} />
            </div>
          <input type="submit" value="Submit" />
        </form>
      );
    }
  }

export default function Home() {
  return (
    <div className="container">
      <Form />

      <style jsx global>{`
        html,
        body {
          padding: 0;
          margin: 0;
          font-family: -apple-system, BlinkMacSystemFont, Segoe UI, Roboto,
            Oxygen, Ubuntu, Cantarell, Fira Sans, Droid Sans, Helvetica Neue,
            sans-serif;
        }

        * {
          box-sizing: border-box;
        }

        .row {
            margin-top: 1%;
            display: flex;
            justify-content: center;
            flex-direction: column;
        }

        input[type=text] {
            width: 50%;
        }

        input[type=submit] {
            width: 10%;
            margin-left: 45%;
            margin-top: 1%;
        }
      `}</style>
    </div>
  )
}
